import{_ as e,c as t,o,d as r}from"./app.e93ef735.js";const f=JSON.parse('{"title":"\u667A\u80FD\u6307\u9488","description":"","frontmatter":{},"headers":[],"relativePath":"ch15-00-smart-pointers.md","lastUpdated":1669088319000}'),c={name:"ch15-00-smart-pointers.md"},s=r('<h1 id="\u667A\u80FD\u6307\u9488" tabindex="-1">\u667A\u80FD\u6307\u9488 <a class="header-anchor" href="#\u667A\u80FD\u6307\u9488" aria-hidden="true">#</a></h1><blockquote><p><a href="https://github.com/rust-lang/book/blob/main/src/ch15-00-smart-pointers.md" target="_blank" rel="noreferrer">ch15-00-smart-pointers.md</a><br> commit 1fedfc4b96c2017f64ecfcf41a0a07e2e815f24f</p></blockquote><p><strong>\u6307\u9488</strong> \uFF08<em>pointer</em>\uFF09\u662F\u4E00\u4E2A\u5305\u542B\u5185\u5B58\u5730\u5740\u7684\u53D8\u91CF\u7684\u901A\u7528\u6982\u5FF5\u3002\u8FD9\u4E2A\u5730\u5740\u5F15\u7528\uFF0C\u6216 \u201C\u6307\u5411\u201D\uFF08points at\uFF09\u4E00\u4E9B\u5176\u4ED6\u6570\u636E\u3002Rust \u4E2D\u6700\u5E38\u89C1\u7684\u6307\u9488\u662F\u7B2C\u56DB\u7AE0\u4ECB\u7ECD\u7684 <strong>\u5F15\u7528</strong>\uFF08<em>reference</em>\uFF09\u3002\u5F15\u7528\u4EE5 <code>&amp;</code> \u7B26\u53F7\u4E3A\u6807\u5FD7\u5E76\u501F\u7528\u4E86\u4ED6\u4EEC\u6240\u6307\u5411\u7684\u503C\u3002\u9664\u4E86\u5F15\u7528\u6570\u636E\u6CA1\u6709\u4EFB\u4F55\u5176\u4ED6\u7279\u6B8A\u529F\u80FD\u3002\u5B83\u4EEC\u4E5F\u6CA1\u6709\u4EFB\u4F55\u989D\u5916\u5F00\u9500\uFF0C\u6240\u4EE5\u5E94\u7528\u5F97\u6700\u591A\u3002</p><p>\u53E6\u4E00\u65B9\u9762\uFF0C<strong>\u667A\u80FD\u6307\u9488</strong>\uFF08<em>smart pointers</em>\uFF09\u662F\u4E00\u7C7B\u6570\u636E\u7ED3\u6784\uFF0C\u4ED6\u4EEC\u7684\u8868\u73B0\u7C7B\u4F3C\u6307\u9488\uFF0C\u4F46\u662F\u4E5F\u62E5\u6709\u989D\u5916\u7684\u5143\u6570\u636E\u548C\u529F\u80FD\u3002\u667A\u80FD\u6307\u9488\u7684\u6982\u5FF5\u5E76\u4E0D\u4E3A Rust \u6240\u72EC\u6709\uFF1B\u5176\u8D77\u6E90\u4E8E C++ \u5E76\u5B58\u5728\u4E8E\u5176\u4ED6\u8BED\u8A00\u4E2D\u3002Rust \u6807\u51C6\u5E93\u4E2D\u4E0D\u540C\u7684\u667A\u80FD\u6307\u9488\u63D0\u4F9B\u4E86\u591A\u4E8E\u5F15\u7528\u7684\u989D\u5916\u529F\u80FD\u3002\u672C\u7AE0\u5C06\u4F1A\u63A2\u7D22\u7684\u4E00\u4E2A\u4F8B\u5B50\u4FBF\u662F <strong>\u5F15\u7528\u8BA1\u6570</strong> \uFF08<em>reference counting</em>\uFF09\u667A\u80FD\u6307\u9488\u7C7B\u578B\uFF0C\u5176\u5141\u8BB8\u6570\u636E\u6709\u591A\u4E2A\u6240\u6709\u8005\u3002\u5F15\u7528\u8BA1\u6570\u667A\u80FD\u6307\u9488\u8BB0\u5F55\u603B\u5171\u6709\u591A\u5C11\u4E2A\u6240\u6709\u8005\uFF0C\u5E76\u5F53\u6CA1\u6709\u4EFB\u4F55\u6240\u6709\u8005\u65F6\u8D1F\u8D23\u6E05\u7406\u6570\u636E\u3002</p><p>\u5728 Rust \u4E2D\uFF0C\u666E\u901A\u5F15\u7528\u548C\u667A\u80FD\u6307\u9488\u7684\u4E00\u4E2A\u989D\u5916\u7684\u533A\u522B\u662F\u5F15\u7528\u662F\u4E00\u7C7B\u53EA\u501F\u7528\u6570\u636E\u7684\u6307\u9488\uFF1B\u76F8\u53CD\uFF0C\u5728\u5927\u90E8\u5206\u60C5\u51B5\u4E0B\uFF0C\u667A\u80FD\u6307\u9488 <strong>\u62E5\u6709</strong> \u4ED6\u4EEC\u6307\u5411\u7684\u6570\u636E\u3002</p><p>\u5B9E\u9645\u4E0A\u672C\u4E66\u4E2D\u5DF2\u7ECF\u51FA\u73B0\u8FC7\u4E00\u4E9B\u667A\u80FD\u6307\u9488\uFF0C\u6BD4\u5982\u7B2C\u516B\u7AE0\u7684 <code>String</code> \u548C <code>Vec&lt;T&gt;</code>\uFF0C\u867D\u7136\u5F53\u65F6\u6211\u4EEC\u5E76\u4E0D\u8FD9\u4E48\u79F0\u547C\u5B83\u4EEC\u3002\u8FD9\u4E9B\u7C7B\u578B\u90FD\u5C5E\u4E8E\u667A\u80FD\u6307\u9488\u56E0\u4E3A\u5B83\u4EEC\u62E5\u6709\u4E00\u4E9B\u6570\u636E\u5E76\u5141\u8BB8\u4F60\u4FEE\u6539\u5B83\u4EEC\u3002\u5B83\u4EEC\u4E5F\u5E26\u6709\u5143\u6570\u636E\uFF08\u6BD4\u5982\u4ED6\u4EEC\u7684\u5BB9\u91CF\uFF09\u548C\u989D\u5916\u7684\u529F\u80FD\u6216\u4FDD\u8BC1\uFF08<code>String</code> \u7684\u6570\u636E\u603B\u662F\u6709\u6548\u7684 UTF-8 \u7F16\u7801\uFF09\u3002</p><p>\u667A\u80FD\u6307\u9488\u901A\u5E38\u4F7F\u7528\u7ED3\u6784\u4F53\u5B9E\u73B0\u3002\u667A\u80FD\u6307\u9488\u533A\u522B\u4E8E\u5E38\u89C4\u7ED3\u6784\u4F53\u7684\u663E\u8457\u7279\u6027\u5728\u4E8E\u5176\u5B9E\u73B0\u4E86 <code>Deref</code> \u548C <code>Drop</code> trait\u3002<code>Deref</code> trait \u5141\u8BB8\u667A\u80FD\u6307\u9488\u7ED3\u6784\u4F53\u5B9E\u4F8B\u8868\u73B0\u7684\u50CF\u5F15\u7528\u4E00\u6837\uFF0C\u8FD9\u6837\u5C31\u53EF\u4EE5\u7F16\u5199\u65E2\u7528\u4E8E\u5F15\u7528\u3001\u53C8\u7528\u4E8E\u667A\u80FD\u6307\u9488\u7684\u4EE3\u7801\u3002<code>Drop</code> trait \u5141\u8BB8\u6211\u4EEC\u81EA\u5B9A\u4E49\u5F53\u667A\u80FD\u6307\u9488\u79BB\u5F00\u4F5C\u7528\u57DF\u65F6\u8FD0\u884C\u7684\u4EE3\u7801\u3002\u672C\u7AE0\u4F1A\u8BA8\u8BBA\u8FD9\u4E9B trait \u4EE5\u53CA\u4E3A\u4EC0\u4E48\u5BF9\u4E8E\u667A\u80FD\u6307\u9488\u6765\u8BF4\u4ED6\u4EEC\u5F88\u91CD\u8981\u3002</p><p>\u8003\u8651\u5230\u667A\u80FD\u6307\u9488\u662F\u4E00\u4E2A\u5728 Rust \u7ECF\u5E38\u88AB\u4F7F\u7528\u7684\u901A\u7528\u8BBE\u8BA1\u6A21\u5F0F\uFF0C\u672C\u7AE0\u5E76\u4E0D\u4F1A\u8986\u76D6\u6240\u6709\u73B0\u5B58\u7684\u667A\u80FD\u6307\u9488\u3002\u5F88\u591A\u5E93\u90FD\u6709\u81EA\u5DF1\u7684\u667A\u80FD\u6307\u9488\u800C\u4F60\u4E5F\u53EF\u4EE5\u7F16\u5199\u5C5E\u4E8E\u4F60\u81EA\u5DF1\u7684\u667A\u80FD\u6307\u9488\u3002\u8FD9\u91CC\u5C06\u4F1A\u8BB2\u5230\u7684\u662F\u6765\u81EA\u6807\u51C6\u5E93\u4E2D\u6700\u5E38\u7528\u7684\u4E00\u4E9B\uFF1A</p><ul><li><code>Box&lt;T&gt;</code>\uFF0C\u7528\u4E8E\u5728\u5806\u4E0A\u5206\u914D\u503C</li><li><code>Rc&lt;T&gt;</code>\uFF0C\u4E00\u4E2A\u5F15\u7528\u8BA1\u6570\u7C7B\u578B\uFF0C\u5176\u6570\u636E\u53EF\u4EE5\u6709\u591A\u4E2A\u6240\u6709\u8005</li><li><code>Ref&lt;T&gt;</code> \u548C <code>RefMut&lt;T&gt;</code>\uFF0C\u901A\u8FC7 <code>RefCell&lt;T&gt;</code> \u8BBF\u95EE\u3002\uFF08 <code>RefCell&lt;T&gt;</code> \u662F\u4E00\u4E2A\u5728\u8FD0\u884C\u65F6\u800C\u4E0D\u662F\u5728\u7F16\u8BD1\u65F6\u6267\u884C\u501F\u7528\u89C4\u5219\u7684\u7C7B\u578B\uFF09\u3002</li></ul><p>\u53E6\u5916\u6211\u4EEC\u4F1A\u6D89\u53CA <strong>\u5185\u90E8\u53EF\u53D8\u6027</strong>\uFF08<em>interior mutability</em>\uFF09\u6A21\u5F0F\uFF0C\u8FD9\u662F\u4E0D\u53EF\u53D8\u7C7B\u578B\u66B4\u9732\u51FA\u6539\u53D8\u5176\u5185\u90E8\u503C\u7684 API\u3002\u6211\u4EEC\u4E5F\u4F1A\u8BA8\u8BBA <strong>\u5F15\u7528\u5FAA\u73AF</strong>\uFF08<em>reference cycles</em>\uFF09\u4F1A\u5982\u4F55\u6CC4\u6F0F\u5185\u5B58\uFF0C\u4EE5\u53CA\u5982\u4F55\u907F\u514D\u3002</p><p>\u8BA9\u6211\u4EEC\u5F00\u59CB\u5427\uFF01</p>',11),n=[s];function a(d,i,p,l,m,g){return o(),t("div",null,n)}const u=e(c,[["render",a]]);export{f as __pageData,u as default};
