import{_ as e,c as t,o as r,d as o}from"./app.a07ec8df.js";const _=JSON.parse('{"title":"\u65E0\u754F\u5E76\u53D1","description":"","frontmatter":{},"headers":[],"relativePath":"ch16-00-concurrency.md","lastUpdated":1669168124000}'),n={name:"ch16-00-concurrency.md"},s=o('<h1 id="\u65E0\u754F\u5E76\u53D1" tabindex="-1">\u65E0\u754F\u5E76\u53D1 <a class="header-anchor" href="#\u65E0\u754F\u5E76\u53D1" aria-hidden="true">#</a></h1><blockquote><p><a href="https://github.com/rust-lang/book/blob/main/src/ch16-00-concurrency.md" target="_blank" rel="noreferrer">ch16-00-concurrency.md</a> <br> commit 1fedfc4b96c2017f64ecfcf41a0a07e2e815f24f</p></blockquote><p>\u5B89\u5168\u4E14\u9AD8\u6548\u7684\u5904\u7406\u5E76\u53D1\u7F16\u7A0B\u662F Rust \u7684\u53E6\u4E00\u4E2A\u4E3B\u8981\u76EE\u6807\u3002<strong>\u5E76\u53D1\u7F16\u7A0B</strong>\uFF08<em>Concurrent programming</em>\uFF09\uFF0C\u4EE3\u8868\u7A0B\u5E8F\u7684\u4E0D\u540C\u90E8\u5206\u76F8\u4E92\u72EC\u7ACB\u7684\u6267\u884C\uFF0C\u800C <strong>\u5E76\u884C\u7F16\u7A0B</strong>\uFF08<em>parallel programming</em>\uFF09\u4EE3\u8868\u7A0B\u5E8F\u4E0D\u540C\u90E8\u5206\u4E8E\u540C\u65F6\u6267\u884C\uFF0C\u8FD9\u4E24\u4E2A\u6982\u5FF5\u968F\u7740\u8BA1\u7B97\u673A\u8D8A\u6765\u8D8A\u591A\u7684\u5229\u7528\u591A\u5904\u7406\u5668\u7684\u4F18\u52BF\u65F6\u663E\u5F97\u6108\u53D1\u91CD\u8981\u3002\u7531\u4E8E\u5386\u53F2\u539F\u56E0\uFF0C\u5728\u6B64\u7C7B\u4E0A\u4E0B\u6587\u4E2D\u7F16\u7A0B\u4E00\u76F4\u662F\u56F0\u96BE\u4E14\u5BB9\u6613\u51FA\u9519\u7684\uFF1ARust \u5E0C\u671B\u80FD\u6539\u53D8\u8FD9\u4E00\u70B9\u3002</p><p>\u8D77\u521D\uFF0CRust \u56E2\u961F\u8BA4\u4E3A\u786E\u4FDD\u5185\u5B58\u5B89\u5168\u548C\u9632\u6B62\u5E76\u53D1\u95EE\u9898\u662F\u4E24\u4E2A\u5206\u522B\u9700\u8981\u4E0D\u540C\u65B9\u6CD5\u5E94\u5BF9\u7684\u6311\u6218\u3002\u968F\u7740\u65F6\u95F4\u7684\u63A8\u79FB\uFF0C\u56E2\u961F\u53D1\u73B0\u6240\u6709\u6743\u548C\u7C7B\u578B\u7CFB\u7EDF\u662F\u4E00\u7CFB\u5217\u89E3\u51B3\u5185\u5B58\u5B89\u5168 <strong>\u548C</strong> \u5E76\u53D1\u95EE\u9898\u7684\u5F3A\u6709\u529B\u7684\u5DE5\u5177\uFF01\u901A\u8FC7\u5229\u7528\u6240\u6709\u6743\u548C\u7C7B\u578B\u68C0\u67E5\uFF0C\u5728 Rust \u4E2D\u5F88\u591A\u5E76\u53D1\u9519\u8BEF\u90FD\u662F <strong>\u7F16\u8BD1\u65F6</strong> \u9519\u8BEF\uFF0C\u800C\u975E\u8FD0\u884C\u65F6\u9519\u8BEF\u3002\u56E0\u6B64\uFF0C\u76F8\u6BD4\u82B1\u8D39\u5927\u91CF\u65F6\u95F4\u5C1D\u8BD5\u91CD\u73B0\u8FD0\u884C\u65F6\u5E76\u53D1 bug \u51FA\u73B0\u7684\u7279\u5B9A\u60C5\u51B5\uFF0CRust \u4F1A\u62D2\u7EDD\u7F16\u8BD1\u4E0D\u6B63\u786E\u7684\u4EE3\u7801\u5E76\u63D0\u4F9B\u89E3\u91CA\u95EE\u9898\u7684\u9519\u8BEF\u4FE1\u606F\u3002\u56E0\u6B64\uFF0C\u4F60\u53EF\u4EE5\u5728\u5F00\u53D1\u65F6\u4FEE\u590D\u4EE3\u7801\uFF0C\u800C\u4E0D\u662F\u5728\u90E8\u7F72\u5230\u751F\u4EA7\u73AF\u5883\u540E\u4FEE\u590D\u4EE3\u7801\u3002\u6211\u4EEC\u7ED9 Rust \u7684\u8FD9\u4E00\u90E8\u5206\u8D77\u4E86\u4E00\u4E2A\u7EF0\u53F7 <strong>\u65E0\u754F\u5E76\u53D1</strong>\uFF08<em>fearless concurrency</em>\uFF09\u3002\u65E0\u754F\u5E76\u53D1\u4EE4\u4F60\u7684\u4EE3\u7801\u514D\u4E8E\u51FA\u73B0\u8BE1\u5F02\u7684 bug \u5E76\u53EF\u4EE5\u8F7B\u677E\u91CD\u6784\u4E14\u65E0\u9700\u62C5\u5FC3\u4F1A\u5F15\u5165\u65B0\u7684 bug\u3002</p><blockquote><p>\u6CE8\u610F\uFF1A\u51FA\u4E8E\u7B80\u6D01\u7684\u8003\u8651\uFF0C\u6211\u4EEC\u5C06\u5F88\u591A\u95EE\u9898\u5F52\u7C7B\u4E3A <strong>\u5E76\u53D1</strong>\uFF0C\u800C\u4E0D\u662F\u66F4\u51C6\u786E\u7684\u533A\u5206 <strong>\u5E76\u53D1\u548C\uFF08\u6216\uFF09\u5E76\u884C</strong>\u3002\u5982\u679C\u8FD9\u662F\u4E00\u672C\u4E13\u6CE8\u4E8E\u5E76\u53D1\u548C/\u6216\u5E76\u884C\u7684\u4E66\uFF0C\u6211\u4EEC\u80AF\u5B9A\u4F1A\u66F4\u52A0\u7CBE\u786E\u7684\u3002\u5BF9\u4E8E\u672C\u7AE0\uFF0C\u5F53\u6211\u4EEC\u8C08\u5230 <strong>\u5E76\u53D1</strong> \u65F6\uFF0C\u8BF7\u81EA\u884C\u8111\u5185\u66FF\u6362\u4E3A <strong>\u5E76\u53D1\u548C\uFF08\u6216\uFF09\u5E76\u884C</strong>\u3002</p></blockquote><p>\u5F88\u591A\u8BED\u8A00\u6240\u63D0\u4F9B\u7684\u5904\u7406\u5E76\u53D1\u95EE\u9898\u7684\u89E3\u51B3\u65B9\u6CD5\u90FD\u975E\u5E38\u6709\u7279\u8272\u3002\u4F8B\u5982\uFF0CErlang \u6709\u7740\u4F18\u96C5\u7684\u6D88\u606F\u4F20\u9012\u5E76\u53D1\u529F\u80FD\uFF0C\u4F46\u53EA\u6709\u6A21\u7CCA\u4E0D\u6E05\u7684\u5728\u7EBF\u7A0B\u95F4\u5171\u4EAB\u72B6\u6001\u7684\u65B9\u6CD5\u3002\u5BF9\u4E8E\u9AD8\u7EA7\u8BED\u8A00\u6765\u8BF4\uFF0C\u53EA\u5B9E\u73B0\u53EF\u80FD\u89E3\u51B3\u65B9\u6848\u7684\u5B50\u96C6\u662F\u4E00\u4E2A\u5408\u7406\u7684\u7B56\u7565\uFF0C\u56E0\u4E3A\u9AD8\u7EA7\u8BED\u8A00\u6240\u8BB8\u8BFA\u7684\u4EF7\u503C\u6765\u6E90\u4E8E\u727A\u7272\u4E00\u4E9B\u63A7\u5236\u6765\u6362\u53D6\u62BD\u8C61\u3002\u7136\u800C\u5BF9\u4E8E\u5E95\u5C42\u8BED\u8A00\u5219\u671F\u671B\u63D0\u4F9B\u5728\u4EFB\u4F55\u7ED9\u5B9A\u7684\u60C5\u51B5\u4E0B\u6709\u7740\u6700\u9AD8\u7684\u6027\u80FD\u4E14\u5BF9\u786C\u4EF6\u6709\u66F4\u5C11\u7684\u62BD\u8C61\u3002\u56E0\u6B64\uFF0CRust \u63D0\u4F9B\u4E86\u591A\u79CD\u5DE5\u5177\uFF0C\u4EE5\u7B26\u5408\u5B9E\u9645\u60C5\u51B5\u548C\u9700\u6C42\u7684\u65B9\u5F0F\u6765\u4E3A\u95EE\u9898\u5EFA\u6A21\u3002</p><p>\u5982\u4E0B\u662F\u672C\u7AE0\u5C06\u8981\u6D89\u53CA\u5230\u7684\u5185\u5BB9\uFF1A</p><ul><li>\u5982\u4F55\u521B\u5EFA\u7EBF\u7A0B\u6765\u540C\u65F6\u8FD0\u884C\u591A\u6BB5\u4EE3\u7801\u3002</li><li><strong>\u6D88\u606F\u4F20\u9012</strong>\uFF08<em>Message passing</em>\uFF09\u5E76\u53D1\uFF0C\u5176\u4E2D\u4FE1\u9053\uFF08channel\uFF09\u88AB\u7528\u6765\u5728\u7EBF\u7A0B\u95F4\u4F20\u9012\u6D88\u606F\u3002</li><li><strong>\u5171\u4EAB\u72B6\u6001</strong>\uFF08<em>Shared state</em>\uFF09\u5E76\u53D1\uFF0C\u5176\u4E2D\u591A\u4E2A\u7EBF\u7A0B\u53EF\u4EE5\u8BBF\u95EE\u540C\u4E00\u7247\u6570\u636E\u3002</li><li><code>Sync</code> \u548C <code>Send</code> trait\uFF0C\u5C06 Rust \u7684\u5E76\u53D1\u4FDD\u8BC1\u6269\u5C55\u5230\u7528\u6237\u5B9A\u4E49\u7684\u4EE5\u53CA\u6807\u51C6\u5E93\u63D0\u4F9B\u7684\u7C7B\u578B\u4E2D\u3002</li></ul>',8),c=[s];function a(g,l,u,i,d,p){return r(),t("div",null,c)}const h=e(n,[["render",a]]);export{_ as __pageData,h as default};
